import React, { Component } from 'react';
import './App.css'; // Import CSS file for styling

class TodoList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todos: [
        { task: 'Take Motrin', completed: false },
        { task: 'Arthritis Stretches', completed: false },
        { task: 'Go for a run', completed: false }
      ],
      inputValue: ''
    };
  }

  handleInputChange = (event) => {
    this.setState({ inputValue: event.target.value });
  };

  handleAddTodo = () => {
    const { inputValue, todos } = this.state;
    if (inputValue.trim() !== '') {
      this.setState({
        todos: [...todos, { task: inputValue, completed: false }],
        inputValue: ''
      });
    }
  };

  handleCheckboxChange = (index) => {
    const { todos } = this.state;
    const updatedTodos = [...todos];
    updatedTodos[index].completed = !updatedTodos[index].completed;
    this.setState({ todos: updatedTodos });
  };

  render() {
    const { todos, inputValue } = this.state;

    return (
      <div className="todo-container">
        <h1>Today's Checklist</h1>
        <ul className="todo-list">
          {todos.map((todo, index) => (
            <li key={index} className={todo.completed ? 'completed' : ''}>
              <input
                type="checkbox"
                checked={todo.completed}
                onChange={() => this.handleCheckboxChange(index)}
              />
              <span>{todo.task}</span>
            </li>
          ))}
        </ul>
        <div className="todo-input">
          <input
            type="text"
            value={inputValue}
            onChange={this.handleInputChange}
            placeholder="Add a new task..."
          />
          <button onClick={this.handleAddTodo}>Add</button>
        </div>
        <button className="talk-button" onClick={() => alert("Talk to me")}>Talk to me</button>
        <button className="Emergency" onClick={() => alert("Call for Help")}>Call for Help</button>
      </div>
    );
  }
}

export default TodoList;
